                // é‡å‘½åæ–‡ä»¶
                fs.rename(oldPath, newPath, err => {
                    if (err) {
                        console.error(`Error renaming file ${file}:`, err);
                    } else {
                        console.log(`Renamed file ${file} to ${newFileName}`);
                    }
                });
            });
        });
    });
}

