 * @param arr å¾…æŽ’åºæ•°ç»„
 * @returns æŽ’åºåŽæ•°ç»„
 */
function countingSort(arr: number[]): number[] {
  const max = Math.max(...arr);
  const count = new Array(max + 1).fill(0);

  for (let i = 0; i < arr.length; i++) {
    count[arr[i]]++;
  }

  const res = [];

  for (let i = 0; i <= max; i++) {
    while (count[i]--) {
      res.push(i);
    }
  }

  return res;
}

