 * @param arr å¾…æŽ’åºæ•°ç»„
 * @param bucketSize æ¡¶å¤§å°
 * @returns æŽ’åºåŽæ•°ç»„
 */
function bucketSort(arr: number[], bucketSize = 5): number[] {
  if (arr.length === 0) {
    return arr;
  }

  // æ‰¾å‡ºæœ€å¤§å€¼å’Œæœ€å°å€¼
  let min = arr[0];
  let max = arr[0];

  for (let i = 1; i < arr.length; i++) {
    if (arr[i] < min) {
      min = arr[i];
    } else if (arr[i] > max) {
      max = arr[i];
    }
  }

  // è®¡ç®—æ¡¶çš„æ•°é‡
  const bucketCount = Math.floor((max - min) / bucketSize) + 1;
  const buckets: number[][] = [];

  for (let i = 0; i < bucketCount; i++) {
    buckets[i] = [];
  }

  // å°†å…ƒç´ åˆ†é…åˆ°æ¡¶ä¸­
  for (let i = 0; i < arr.length; i++) {
    const index = Math.floor((arr[i] - min) / bucketSize);
    buckets[index].push(arr[i]);
  }

  // å¯¹æ¯ä¸ªæ¡¶è¿›è¡ŒæŽ’åºï¼Œå¹¶å°†ç»“æžœåˆå¹¶
  const res = [];

  for (let i = 0; i < buckets.length; i++) {
    if (buckets[i]) {
      const sortedBucket = countingSort(buckets[i]);

      for (let j = 0; j < sortedBucket.length; j++) {
        res.push(sortedBucket[j]);
      }
    }
  }

  return res;
}

```
