    for (let j = 0; j < arr.length; j++) {
      const num = arr[j];
      const digit = Math.floor(num / Math.pow(10, i)) % 10;
      buckets[digit].push(num);
    }

    arr = [];

    for (let k = 0; k < buckets.length; k++) {
      while (buckets[k].length) {
        arr.push(buckets[k].shift()!);
      }
    }
  }

  return arr;
}

