const fs = require('fs');
const path = require('path');

function renameImages(directory) {
    // è¯»å–ç›®å½•ä¸‹çš„æ‰€æœ‰æ–‡ä»¶
    fs.readdir(directory, (err, files) => {
        if (err) {
            console.error('Error reading directory:', err);
            return;
        }

        // éå†æ‰€æœ‰æ–‡ä»¶
        files.forEach((file, index) => {
            // è·å–æ–‡ä»¶çš„å®Œæ•´è·¯å¾„
            const oldPath = path.join(directory, file);

            // åˆ¤æ–­æ˜¯å¦ä¸ºæ–‡ä»¶å¤¹ï¼Œå¦‚æœæ˜¯åˆ™é€’å½’è°ƒç”¨è¯¥æ–¹æ³•
            fs.stat(oldPath, (err, stats) => {
                if (err) {
                    console.error(`Error stating file ${file}:`, err);
                    return;
                }

                if (stats.isDirectory()) {
                    renameImages(oldPath);
                    return;
                }

                // ç”Ÿæˆæ–°çš„æ–‡ä»¶å
                const extension = path.extname(file); // è·å–æ–‡ä»¶æ‰©å±•å
                const newFileName = `${index + 1}${extension}`; // ä¿®æ”¹ä¸ºä½ æƒ³è¦çš„å‘½åæ ¼å¼

                // æ„å»ºæ–°çš„æ–‡ä»¶è·¯å¾„
                const newPath = path.join(directory, newFileName);

                // é‡å‘½åæ–‡ä»¶
                fs.rename(oldPath, newPath, err => {
                    if (err) {
                        console.error(`Error renaming file ${file}:`, err);
                    } else {
                        console.log(`Renamed file ${file} to ${newFileName}`);
                    }
                });
            });
        });
    });
}

// è°ƒç”¨æ–¹æ³•ï¼Œä¼ å…¥å›¾ç‰‡æ‰€åœ¨çš„ç›®å½•è·¯å¾„
renameImages('./img');

